import React, { useState } from 'react';
import './adminaccess.css';
import { useNavigate } from 'react-router-dom';
import AdminRegister from './AdminRegister'; // Import the new Registration component

const AdminLogin = ({ closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showRegister, setShowRegister] = useState(false); // State for toggling between forms
  const navigate = useNavigate();

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    console.log('Admin Username:', username);
    console.log('Admin Password:', password);
    
    // After successful login, navigate to the admin dashboard
    navigate('/admin-dashboard');
    closeModal();
  };

  return (
    <div>
      {showRegister ? (
        <AdminRegister closeModal={() => setShowRegister(false)} /> // Show registration form
      ) : (
        <div className="admin-modal">
          <div className="admin-modal-content">
            <h2>Admin Login</h2>
            <span className="admin-close" onClick={closeModal}>&times;</span>
            <form onSubmit={handleLoginSubmit}>
              <div className="input-box">
                <input
                  type="text"
                  placeholder="Username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <i className="bx bxs-user"></i>
              </div>
              <div className="input-box">
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <i className="bx bxs-lock-alt"></i>
              </div>
              
              <div className="remember-forget">
                <a href="#">Forgot Password?</a>
              </div>
              <button type="submit" className="btn">Login</button>
            </form>
            <div className="register-link">
              <p>Don't have an account? <a href="#" onClick={() => setShowRegister(true)}>Register</a></p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminLogin;
