import React, { useState } from 'react';
import './adminaccess.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios

const AdminLogin = ({ closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/admin/login', {
        username,
        password
      });

      // If login is successful, navigate to the admin dashboard
      if (response.status === 200) {
        console.log('Login successful:', response.data);
        navigate('/admin-dashboard');
      }
    } catch (error) {
      console.error('Login failed:', error.response ? error.response.data : error.message);
      alert('Invalid username or password'); // You can display a more user-friendly message
    } finally {
      closeModal(); // Close modal regardless of success or failure
    }
  

    // Directly navigate to the admin dashboard for testing
    navigate('/admin-dashboard');
    closeModal(); // Close modal
  };

  return (
    <div className="admin-modal">
      <div className="admin-modal-content">
        <h2>Admin Login</h2>
        <span className="admin-close" onClick={closeModal}>&times;</span>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <i className="bx bxs-user"></i>
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="remember-forget">
            <label>
              <input type="checkbox" />
              Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="btn">Login</button>
        </form>
        <div className="register-link">
          <p>Don't have an account? <a href="#">Register</a></p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
