import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './home.css'; // Assuming the styles are shared

const SideMenu = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const openMenu = () => {
    setMenuOpen(true);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  return (
    <div>
      {/* Side Menu */}
      <ul id="sidemenu" className={menuOpen ? 'open' : 'closed'}>
        <li><a href="#header">Home</a></li>
        <li><Link to="/about">About</Link></li> {/* Moved About here */}
        <li><Link to="/admin-login">Admin Login</Link></li> {/* Moved Admin Login here */}
        {/* Close Icon */}
        <i className="fas fa-times" onClick={closeMenu} style={{ cursor: 'pointer' }}></i>
      </ul>

      {/* Menu Open Icon */}
      <i className="fas fa-bars" onClick={openMenu} style={{ cursor: 'pointer' }}></i>
    </div>
  );
};

export default SideMenu;
