import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './home.css'; // Assuming the styles are shared
import AdminAccess from './AdminAccess';

const SideMenu = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const openMenu = () => {
    setMenuOpen(true);
  };

  const closeMenu = () => {
    setMenuOpen(false);
  };

  const openModal = () => setIsModalOpen(true);

  // Function to close the AdminLogin modal
  const closeModal = () => setIsModalOpen(false);
  


  return (
    <div>
      {/* Menu Open Icon */}
      <i className="fas fa-bars menu-open-icon" onClick={openMenu}></i>

      {/* Side Menu */}
      <div id="sidemenu" className={menuOpen ? 'open' : 'closed'}>
        <i className="fas fa-times close-icon" onClick={closeMenu}></i> {/* Close Icon */}
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/about">About</Link></li> {/* Moved About here */}
          <li><Link to="/contact">Contact</Link></li>
          <li><Link to="/book-tickets">Book Tickets</Link></li>
          <li><Link to="/admin-login">Admin Login</Link></li> {/* Moved Admin Login here */}
        </ul>
      </div>
      <div>
        {/* Render the AdminLogin modal if isModalOpen is true */}
      {isModalOpen && <AdminAccess closeModal={closeModal} />}
      </div>
    </div>
  );
};

export default SideMenu;
