import React, { useState } from 'react';
import './admindashboard.css';

const AdminDashboard = () => {
  const [activeSection, setActiveSection] = useState(null); // Track whether Flight or Passenger is active
  const [activeAction, setActiveAction] = useState(null);   // Track Add or Display action

  const renderFlightSection = () => (
    <div className="action-buttons">
      <button onClick={() => setActiveAction('addFlight')}>Add Flight</button>
      <button onClick={() => setActiveAction('displayFlight')}>Display Flights</button>
      {activeAction === 'addFlight' && (
        <div className="form-section">
          <h2>Add Flight</h2>
          <label>Flight Number: <input type="text" /></label>
          <label>Destination: <input type="text" /></label>
          <label>Status: <input type="text" /></label>
        </div>
      )}
      {activeAction === 'displayFlight' && (
        <div className="list-section">
          <h2>Flight List</h2>
          {/* Flight display data here */}
        </div>
      )}
    </div>
  );

  const renderPassengerSection = () => (
    <div className="action-buttons">
      <button onClick={() => setActiveAction('addPassenger')}>Add Passenger</button>
      <button onClick={() => setActiveAction('displayPassenger')}>Display Passengers</button>
      {activeAction === 'addPassenger' && (
        <div className="form-section">
          <h2>Add Passenger</h2>
          <label>Name: <input type="text" /></label>
          <label>Passenger Number: <input type="text" /></label>
          <label>Flight ID: <input type="text" /></label>
        </div>
      )}
      {activeAction === 'displayPassenger' && (
        <div className="list-section">
          <h2>Passenger List</h2>
          {/* Passenger display data here */}
        </div>
      )}
    </div>
  );

  return (
    <div className="admin-dashboard">
      <div className="top-container">
        <button onClick={() => { setActiveSection('flight'); setActiveAction(null); }}>
          Flights
        </button>
        <button onClick={() => { setActiveSection('passenger'); setActiveAction(null); }}>
          Passengers
        </button>
      </div>
      
      <div className="main-section">
        {activeSection === 'flight' && renderFlightSection()}
        {activeSection === 'passenger' && renderPassengerSection()}
      </div>
    </div>
  );
};

export default AdminDashboard;
