import React, { useState } from 'react';
import './admindashboard.css';

const AdminDashboard = () => {
  const [view, setView] = useState('');
  const [flightView, setFlightView] = useState('');
  const [passengerView, setPassengerView] = useState('');
  const [arrivalsDeparturesView, setArrivalsDeparturesView] = useState('');

  const [flights, setFlights] = useState([]);
  const [passengers, setPassengers] = useState([]);

  const [newFlight, setNewFlight] = useState({ flightNumber: '', destination: '', status: '' });
  const [newPassenger, setNewPassenger] = useState({ name: '', passengerNumber: '', flightId: '' });

  const [isEditingFlight, setIsEditingFlight] = useState(null);
  const [isEditingPassenger, setIsEditingPassenger] = useState(null);

  const handleAddFlight = (e) => {
    e.preventDefault();
    if (isEditingFlight !== null) {
      const updatedFlights = flights.map((flight, index) =>
        index === isEditingFlight ? newFlight : flight
      );
      setFlights(updatedFlights);
      setIsEditingFlight(null);
    } else {
      setFlights([...flights, newFlight]);
    }
    setNewFlight({ flightNumber: '', destination: '', status: '' });
  };

  const handleAddPassenger = (e) => {
    e.preventDefault();
    if (isEditingPassenger !== null) {
      const updatedPassengers = passengers.map((passenger, index) =>
        index === isEditingPassenger ? newPassenger : passenger
      );
      setPassengers(updatedPassengers);
      setIsEditingPassenger(null);
    } else {
      setPassengers([...passengers, newPassenger]);
    }
    setNewPassenger({ name: '', passengerNumber: '', flightId: '' });
  };

  const handleEditFlight = (index) => {
    setIsEditingFlight(index);
    setNewFlight(flights[index]);
  };

  const handleEditPassenger = (index) => {
    setIsEditingPassenger(index);
    setNewPassenger(passengers[index]);
  };

  const handleRemoveFlight = (index) => {
    setFlights(flights.filter((_, i) => i !== index));
  };

  const handleRemovePassenger = (index) => {
    setPassengers(passengers.filter((_, i) => i !== index));
  };

  return (
    <div className="admin-dashboard">
      <div className="top-container">
        <button onClick={() => setView('flights')} className={view === 'flights' ? 'active' : ''}>
          Flights
        </button>
        <button onClick={() => setView('passengers')} className={view === 'passengers' ? 'active' : ''}>
          Passengers
        </button>
      </div>

      <div className="section-content">
        {view === 'flights' && (
          <div>
            {/* Arrivals and Departures buttons */}
            <div className="section-buttons">
              <button onClick={() => setArrivalsDeparturesView('arrivals')}>
                Arrivals
              </button>
              <button onClick={() => setArrivalsDeparturesView('departures')}>
                Departures
              </button>
            </div>

            {/* Show Add/Display Flight buttons only after selecting Arrivals or Departures */}
            {arrivalsDeparturesView && (
              <div className="section-buttons">
                <button onClick={() => setFlightView('add')}>Add Arrival Flight</button>
                <button onClick={() => setFlightView('display')}>Display Arrival Flight</button>
              </div>
            )}


            {/* Add flight form */}
            {flightView === 'add' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Flight' : 'Add Flight'}</h2>
                  <div className="form-group">
                    <label>
                      Flight Number:
                      <input
                        type="text"
                        value={newFlight.flightNumber}
                        onChange={(e) => setNewFlight({ ...newFlight, flightNumber: e.target.value })}
                      />
                    </label>
                    <label>
                      Orig:
                      <input
                        type="text"
                        value={newFlight.destination}
                        onChange={(e) => setNewFlight({ ...newFlight, destination: e.target.value })}
                      />
                    </label>
                    <label>
                      Status:
                      <input
                        type="text"
                        value={newFlight.status}
                        onChange={(e) => setNewFlight({ ...newFlight, status: e.target.value })}
                      />
                    </label>
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Show Add/Display Flight buttons only after selecting Arrivals or Departures */}
            {arrivalsDeparturesView && (
              <div className="section-buttons">
                <button onClick={() => setFlightView('add')}>Add Departure Flight</button>
                <button onClick={() => setFlightView('display')}>Display Departure Flight</button>
              </div>
            )}

{flightView === 'add' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Flight' : 'Add Flight'}</h2>
                  <div className="form-group">
                    <label>
                      Flight Number:
                      <input
                        type="text"
                        value={newFlight.flightNumber}
                        onChange={(e) => setNewFlight({ ...newFlight, flightNumber: e.target.value })}
                      />
                    </label>
                    <label>
                      Destination:
                      <input
                        type="text"
                        value={newFlight.destination}
                        onChange={(e) => setNewFlight({ ...newFlight, destination: e.target.value })}
                      />
                    </label>
                    <label>
                      Status:
                      <input
                        type="text"
                        value={newFlight.status}
                        onChange={(e) => setNewFlight({ ...newFlight, status: e.target.value })}
                      />
                    </label>
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}


            {/* Display flights list */}
            {flightView === 'display' && (
              <div className="flights-list">
                <h3>Flights:</h3>
                {flights.map((flight, index) => (
                  <div key={index} className="data-row">
                    <span>Flight {flight.flightNumber} to {flight.destination} - {flight.status}</span>
                    <div className="actions">
                      <button onClick={() => handleEditFlight(index)}>
                        <i className="fa-solid fa-pen"></i>
                      </button>
                      <button onClick={() => handleRemoveFlight(index)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {view === 'passengers' && (
          <div>
            <div className="section-buttons">
              <button onClick={() => setPassengerView('add')}>Add Passengers</button>
              <button onClick={() => setPassengerView('display')}>Display Passengers</button>
            </div>

            {passengerView === 'add' && (
              <div className="passenger-section">
                <form className="passenger-form" onSubmit={handleAddPassenger}>
                  <h2>{isEditingPassenger !== null ? 'Edit Passenger' : 'Add Passenger'}</h2>
                  <div className="form-group">
                    <label>
                      Name:
                      <input
                        type="text"
                        value={newPassenger.name}
                        onChange={(e) => setNewPassenger({ ...newPassenger, name: e.target.value })}
                      />
                    </label>
                    <label>
                      Email:
                      <input
                        type="email"
                        value={newPassenger.email}
                        onChange={(e) => setNewPassenger({ ...newPassenger, email: e.target.value })}
                      />
                    </label>
                    <label>
                      Phone:
                      <input
                        type="text"
                        value={newPassenger.phone}
                        onChange={(e) => setNewPassenger({ ...newPassenger, phone: e.target.value })}
                      />
                    </label>
                    <label>
                      Flight:
                      <input
                        type="text"
                        value={newPassenger.flight}
                        onChange={(e) => setNewPassenger({ ...newPassenger, flight: e.target.value })}
                      />
                    </label>
                    <label>
                      Location:
                      <input
                        type="text"
                        value={newPassenger.location}
                        onChange={(e) => setNewPassenger({ ...newPassenger, location: e.target.value })}
                      />
                    </label>
                    <label>
                      Date:
                      <input
                        type="date"
                        value={newPassenger.date}
                        onChange={(e) => setNewPassenger({ ...newPassenger, date: e.target.value })}
                      />
                    </label>
                    <button type="submit">{isEditingPassenger !== null ? 'Update Passenger' : '+ Add Passenger'}</button>
                  </div>
                </form>
              </div>
            )}

            {passengerView === 'display' && (
              <div className="passengers-list">
                <h3>Passengers:</h3>
                {passengers.map((passenger, index) => (
                  <div key={index} className="data-row">
                    <span>{passenger.name} - Passport {passenger.passengerNumber} - Flight ID: {passenger.flightId}</span>
                    <div className="actions">
                      <button onClick={() => handleEditPassenger(index)}>
                        <i className="fa-solid fa-pen"></i>
                      </button>
                      <button onClick={() => handleRemovePassenger(index)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
