import React, { useState } from 'react';
import './admindashboard.css'; // Ensure you have this CSS file

const AdminDashboard = () => {
  const [view, setView] = useState('flights'); // Manage view between flights and passengers
  const [flightType, setFlightType] = useState(null); // Manage flight type (arrival/departure)
  const [showDisplay, setShowDisplay] = useState(false); // Manage whether to show display section

  const [arrivals, setArrivals] = useState([]);
  const [departures, setDepartures] = useState([]);
  const [newArrival, setNewArrival] = useState({ fno: '', arr_time: '', flight: '', origin: '', status: '' });
  const [newDeparture, setNewDeparture] = useState({ fno: '', dept_time: '', flight: '', destination: '', status: '' });

  const [passengers, setPassengers] = useState([]);
  const [newPassenger, setNewPassenger] = useState({ name: '', passengerNumber: '', flightId: '' });

  // Functions for managing Arrivals
  const handleAddArrival = () => {
    setArrivals([...arrivals, newArrival]);
    setNewArrival({ fno: '', arr_time: '', flight: '', origin: '', status: '' });
  };

  const handleRemoveArrival = (index) => {
    setArrivals(arrivals.filter((_, i) => i !== index));
  };

  // Functions for managing Departures
  const handleAddDeparture = () => {
    setDepartures([...departures, newDeparture]);
    setNewDeparture({ fno: '', dept_time: '', flight: '', destination: '', status: '' });
  };

  const handleRemoveDeparture = (index) => {
    setDepartures(departures.filter((_, i) => i !== index));
  };

  // Functions for managing Passengers
  const handleAddPassenger = (e) => {
    e.preventDefault();
    setPassengers([...passengers, newPassenger]);
    setNewPassenger({ name: '', passengerNumber: '', flightId: '' });
  };

  const handleRemovePassenger = (index) => {
    setPassengers(passengers.filter((_, i) => i !== index));
  };

  return (
    <div className="admin-dashboard">
      {/* Fixed top container */}
      <div className="top-container">
        <button onClick={() => setView('flights')} className={view === 'flights' ? 'active' : ''}>
          Flights
        </button>
        <button onClick={() => setView('passengers')} className={view === 'passengers' ? 'active' : ''}>
          Passengers
        </button>
      </div>

      {/* Conditional rendering of flight or passenger section */}
      <div className="section-content">
        {view === 'flights' && (
          <>
            <div className="flight-buttons">
              <button onClick={() => { setFlightType('arrival'); setShowDisplay(false); }}>
                Arrival
              </button>
              <button onClick={() => { setFlightType('departure'); setShowDisplay(false); }}>
                Departure
              </button>
            </div>

            {flightType && (
              <div className="flight-management">
                <div className="section-buttons">
                  <button onClick={() => setShowDisplay(false)}>Add Flight</button>
                  <button onClick={() => setShowDisplay(true)}>Display Flight</button>
                </div>

                {/* Display flight data in a formatted view */}
                {showDisplay && (
                  <div className="flight-section">
                    <h2>{flightType.charAt(0).toUpperCase() + flightType.slice(1)}s:</h2>
                    <div className="table">
                      <div className="table-header">
                        <div>Flight No</div>
                        <div>{flightType === 'arrival' ? 'Arrival Time' : 'Departure Time'}</div>
                        <div>Flight</div>
                        <div>{flightType === 'arrival' ? 'Origin' : 'Destination'}</div>
                        <div>Status</div>
                        <div>Actions</div>
                      </div>
                      {(flightType === 'arrival' ? arrivals : departures).map((item, index) => (
                        <div key={index} className="table-row">
                          <div>{item.fno}</div>
                          <div>{flightType === 'arrival' ? item.arr_time : item.dept_time}</div>
                          <div>{item.flight}</div>
                          <div>{flightType === 'arrival' ? item.origin : item.destination}</div>
                          <div>{item.status}</div>
                          <div>
                            <button onClick={() => console.log(`Update ${flightType} at index`, index)}>Update</button>
                            <button onClick={() => (flightType === 'arrival' ? handleRemoveArrival(index) : handleRemoveDeparture(index))}>Cancel</button>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Add flight section */}
                {!showDisplay && flightType === 'arrival' && (
                  <div className="flight-section">
                    <h2>Add Arrival</h2>
                    <div className="form-group">
                      <label>Flight No:</label>
                      <input
                        type="text"
                        value={newArrival.fno}
                        onChange={(e) => setNewArrival({ ...newArrival, fno: e.target.value })}
                      />
                      <label>Arrival Time:</label>
                      <input
                        type="text"
                        value={newArrival.arr_time}
                        onChange={(e) => setNewArrival({ ...newArrival, arr_time: e.target.value })}
                      />
                      <label>Flight:</label>
                      <input
                        type="text"
                        value={newArrival.flight}
                        onChange={(e) => setNewArrival({ ...newArrival, flight: e.target.value })}
                      />
                      <label>Origin:</label>
                      <input
                        type="text"
                        value={newArrival.origin}
                        onChange={(e) => setNewArrival({ ...newArrival, origin: e.target.value })}
                      />
                      <label>Status:</label>
                      <input
                        type="text"
                        value={newArrival.status}
                        onChange={(e) => setNewArrival({ ...newArrival, status: e.target.value })}
                      />
                      <button onClick={handleAddArrival}>+ Add Flight</button>
                    </div>
                  </div>
                )}

                {!showDisplay && flightType === 'departure' && (
                  <div className="flight-section">
                    <h2>Add Departure</h2>
                    <div className="form-group">
                      <label>Flight No:</label>
                      <input
                        type="text"
                        value={newDeparture.fno}
                        onChange={(e) => setNewDeparture({ ...newDeparture, fno: e.target.value })}
                      />
                      <label>Departure Time:</label>
                      <input
                        type="text"
                        value={newDeparture.dept_time}
                        onChange={(e) => setNewDeparture({ ...newDeparture, dept_time: e.target.value })}
                      />
                      <label>Flight:</label>
                      <input
                        type="text"
                        value={newDeparture.flight}
                        onChange={(e) => setNewDeparture({ ...newDeparture, flight: e.target.value })}
                      />
                      <label>Destination:</label>
                      <input
                        type="text"
                        value={newDeparture.destination}
                        onChange={(e) => setNewDeparture({ ...newDeparture, destination: e.target.value })}
                      />
                      <label>Status:</label>
                      <input
                        type="text"
                        value={newDeparture.status}
                        onChange={(e) => setNewDeparture({ ...newDeparture, status: e.target.value })}
                      />
                      <button onClick={handleAddDeparture}>+ Add Flight</button>
                    </div>
                  </div>
                )}
              </div>
            )}
          </>
        )}

        {view === 'passengers' && (
          <div className="passenger-section">
            <h2>Passengers:</h2>
            <div className="passenger-form">
              <label>Name:</label>
              <input
                type="text"
                value={newPassenger.name}
                onChange={(e) => setNewPassenger({ ...newPassenger, name: e.target.value })}
              />
              <label>Passenger Number:</label>
              <input
                type="text"
                value={newPassenger.passengerNumber}
                onChange={(e) => setNewPassenger({ ...newPassenger, passengerNumber: e.target.value })}
              />
              <label>Flight ID:</label>
              <input
                type="text"
                value={newPassenger.flightId}
                onChange={(e) => setNewPassenger({ ...newPassenger, flightId: e.target.value })}
              />
              <button onClick={handleAddPassenger}>+ Add Passenger</button>
            </div>

            {/* Display passenger list in table format */}
            <div className="table">
              <div className="table-header">
                <div>Name</div>
                <div>Passenger Number</div>
                <div>Flight ID</div>
                <div>Actions</div>
              </div>
              {passengers.map((passenger, index) => (
                <div key={index} className="table-row">
                  <div>{passenger.name}</div>
                  <div>{passenger.passengerNumber}</div>
                  <div>{passenger.flightId}</div>
                  <div>
                    <button onClick={() => console.log(`Update passenger at index`, index)}>Update</button>
                    <button onClick={() => handleRemovePassenger(index)}>Remove</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
