import React, { useState } from 'react';
import './admindashboard.css';

const AdminDashboard = () => {
  const [view, setView] = useState('');
  const [flightView, setFlightView] = useState('');
  const [passengerView, setPassengerView] = useState('');
  const [arrivalsDeparturesView, setArrivalsDeparturesView] = useState('');
  
  const [arrivals, setArrivals] = useState([]);
  const [departures, setDepartures] = useState([]);
  const [passengers, setPassengers] = useState([]);
  const [newArrivalFlight, setNewArrivalFlight] = useState({ flightNumber: '', destination: '', status: '' });
  const [newDepartureFlight, setNewDepartureFlight] = useState({ flightNumber: '', destination: '', status: '' });
  const [newPassenger, setNewPassenger] = useState({ name: '', email: '', phone: '', flight: '' });
  const [isEditingFlight, setIsEditingFlight] = useState(null);
  const [isEditingPassenger, setIsEditingPassenger] = useState(null);

  // Function to add arrival flight
  const handleAddArrivalFlight = (e) => {
    e.preventDefault();
    if (isEditingFlight !== null) {
      const updatedArrivals = arrivals.map((flight, index) =>
        index === isEditingFlight ? newArrivalFlight : flight
      );
      setArrivals(updatedArrivals);
      setIsEditingFlight(null);
    } else {
      setArrivals([...arrivals, newArrivalFlight]);
    }
    setNewArrivalFlight({ flightNumber: '', destination: '', status: '' });
  };

  // Function to add departure flight (Separate form)
  const handleAddDepartureFlight = (e) => {
    e.preventDefault();
    if (isEditingFlight !== null) {
      const updatedDepartures = departures.map((flight, index) =>
        index === isEditingFlight ? newDepartureFlight : flight
      );
      setDepartures(updatedDepartures);
      setIsEditingFlight(null);
    } else {
      setDepartures([...departures, newDepartureFlight]);
    }
    setNewDepartureFlight({ flightNumber: '', destination: '', status: '' });
  };

  // Function to add passenger
  const handleAddPassenger = (e) => {
    e.preventDefault();
    if (isEditingPassenger !== null) {
      const updatedPassengers = passengers.map((passenger, index) =>
        index === isEditingPassenger ? newPassenger : passenger
      );
      setPassengers(updatedPassengers);
      setIsEditingPassenger(null);
    } else {
      setPassengers([...passengers, newPassenger]);
    }
    setNewPassenger({ name: '', email: '', phone: '', flight: '' });
  };

  // Function to edit a passenger
  const handleEditPassenger = (index) => {
    setNewPassenger(passengers[index]);
    setIsEditingPassenger(index);
  };

  // Function to remove a passenger
  const handleRemovePassenger = (index) => {
    setPassengers(passengers.filter((_, i) => i !== index));
  };

  // Function to remove flight
  const handleRemoveFlight = (index, type) => {
    if (type === 'arrivals') {
      setArrivals(arrivals.filter((_, i) => i !== index));
    } else if (type === 'departures') {
      setDepartures(departures.filter((_, i) => i !== index));
    }
  };

  const handleArrivalsDeparturesToggle = (type) => {
    setArrivalsDeparturesView(type);
    setFlightView('');
  };

  return (
    <div className="admin-dashboard">
      <div className="top-container">
        <button onClick={() => setView('flights')} className={view === 'flights' ? 'active' : ''}>
          Flights
        </button>
        <button onClick={() => setView('passengers')} className={view === 'passengers' ? 'active' : ''}>
          Passengers
        </button>
      </div>

      <div className="section-content">
        {view === 'flights' && (
          <div>
            {/* Arrivals and Departures buttons */}
            <div className="section-buttons">
              <button onClick={() => handleArrivalsDeparturesToggle('arrivals')}>Arrivals</button>
              <button onClick={() => handleArrivalsDeparturesToggle('departures')}>Departures</button>
            </div>

            {/* Show Add/Display Flight buttons based on selected Arrivals or Departures */}
            {arrivalsDeparturesView && (
              <div className="section-buttons">
                <button onClick={() => setFlightView('add')}>Add {arrivalsDeparturesView === 'arrivals' ? 'Arrival' : 'Departure'} Flight</button>
                <button onClick={() => setFlightView('display')}>Display {arrivalsDeparturesView === 'arrivals' ? 'Arrival' : 'Departure'} Flight</button>
              </div>
            )}

            {/* Add flight form */}
            {flightView === 'add' && arrivalsDeparturesView === 'arrivals' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddArrivalFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Arrival Flight' : 'Add Arrival Flight'}</h2>
                  <div className="form-group">
                  <label>Arrival-Time:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.arrivaltime}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, arrivaltime: e.target.value })}
                    />
                    <label>Flight Name:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.flightName}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, flightName: e.target.value })}
                    />
                    <label>Flight Number:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.flightNumber}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, flightNumber: e.target.value })}
                    />
                    <label>Origin:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.destination}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, destination: e.target.value })}
                    />
                    <label>Status:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.status}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, status: e.target.value })}
                    />
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Add Departure flight form (Separate Form for Departures) */}
            {flightView === 'add' && arrivalsDeparturesView === 'departures' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddDepartureFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Departure Flight' : 'Add Departure Flight'}</h2>
                  <div className="form-group">
                    <label>Departure-Time:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.departuretime}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, departuretime: e.target.value })}
                    />
                    <label>Flight Name:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.flightName}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, flightName: e.target.value })}
                    />
                    <label>Flight Number:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.flightNumber}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, flightNumber: e.target.value })}
                    />
                    <label>Destination:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.destination}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, destination: e.target.value })}
                    />
                    <label>Status:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.status}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, status: e.target.value })}
                    />
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Display flights list */}
            {flightView === 'display' && (
              <div className="flights-list">
                <h3>{arrivalsDeparturesView === 'arrivals' ? 'Arrival Flights' : 'Departure Flights'}:</h3>
                {(arrivalsDeparturesView === 'arrivals' ? arrivals : departures).map((flight, index) => (
                  <div key={index} className="data-row">
                    <span>Flight Name:{flight.flightName} - Flight Number:{flight.flightNumber} - Origin:{flight.origin} - Desti{flight.destination} Status:{flight.status}</span>
                    <div className="actions">
                      <button onClick={() => setIsEditingFlight(index)}>
                        <i className="fa-solid fa-pen"></i>
                      </button>
                      <button onClick={() => handleRemoveFlight(index, arrivalsDeparturesView)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {view === 'passengers' && (
          <div>
            <div className="section-buttons">
              <button onClick={() => setPassengerView('add')}>Add Passengers</button>
              <button onClick={() => setPassengerView('display')}>Display Passengers</button>
            </div>

            {/* Add passenger form */}
            {passengerView === 'add' && (
              <div className="passenger-section">
                <form className="passenger-form" onSubmit={handleAddPassenger}>
                  <h2>{isEditingPassenger !== null ? 'Edit Passenger' : 'Add Passenger'}</h2>
                  <div className="form-group">
                    <label>
                      Name:
                      <input
                        type="text"
                        value={newPassenger.name}
                        onChange={(e) => setNewPassenger({ ...newPassenger, name: e.target.value })}
                      />
                    </label>
                    <label>
                      Email:
                      <input
                        type="email"
                        value={newPassenger.email}
                        onChange={(e) => setNewPassenger({ ...newPassenger, email: e.target.value })}
                      />
                    </label>
                    <label>
                      Phone:
                      <input
                        type="text"
                        value={newPassenger.phone}
                        onChange={(e) => setNewPassenger({ ...newPassenger, phone: e.target.value })}
                      />
                    </label>
                    <label>
                      Flight:
                      <input
                        type="text"
                        value={newPassenger.flight}
                        onChange={(e) => setNewPassenger({ ...newPassenger, flight: e.target.value })}
                      />
                    </label>
                    <label>
                      Location:
                      <input
                        type="text"
                        value={newPassenger.location}
                        onChange={(e) => setNewPassenger({ ...newPassenger, location: e.target.value })}
                      />
                    </label>
                    <label>
                      Date:
                      <input
                        type="date"
                        value={newPassenger.date}
                        onChange={(e) => setNewPassenger({ ...newPassenger, date: e.target.value })}
                      />
                    </label>
                    <button type="submit">{isEditingPassenger !== null ? 'Update Passenger' : '+ Add Passenger'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Display passengers list */}
            {passengerView === 'display' && (
              <div className="passengers-list">
                <h3>Passenger List:</h3>
                {passengers.map((passenger, index) => (
                  <div key={index} className="data-row">
                    <span>{passenger.name} - {passenger.email} - {passenger.phone} - Flight {passenger.flight}</span>
                    <div className="actions">
                      <button onClick={() => handleEditPassenger(index)}>
                        <i className="fa-solid fa-pen"></i>
                      </button>
                      <button onClick={() => handleRemovePassenger(index)}>
                        <i className="fa-solid fa-trash"></i>
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
