import React from 'react';
import './about.css';
import SideMenu from './SideMenu';
import Header from './header';

const About = () => {
  return (
    <div className="about-container">
      <div className="text-content">
        <h2>About Us</h2>
        <p>
          Welcome to the Airport Directory App, your one-stop platform for all airport-related information. Whether you're a traveler or managing logistics, our app ensures you have access to essential services and details at your fingertips.
        </p>
        <h3>Our Mission</h3>
        <p>
          At Airport Directory, our goal is to provide seamless access to real-time flight information, making it easier for travelers to plan their journeys. We aim to bridge the gap between passengers and their travel needs with a user-friendly, accessible platform.
        </p>
        <h3>Features:</h3>
        <ul>
          <li>Flight Arrivals and Departures: View real-time updates on all incoming and outgoing flights, including any delays or gate changes.</li>
          <li>Ticket Booking: Easily book tickets directly from our app with a streamlined, hassle-free process.</li>
          <li>Search Functionality: Quickly search for flight details by entering your flight number or destination.</li>
          <li>Admin Section: Dedicated tools for administrators to manage flights, users, and bookings.</li>
        </ul>
        <h3>Why Choose Us?</h3>
        <p>
          We believe in making airport navigation and flight information access as simple as possible. Our app combines advanced technology with ease of use, providing real-time updates and essential travel services in one place.
        </p>
      </div>
      <SideMenu />
    </div>
  );
};

export default About;
export {SideMenu};
