import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './admindashboard.css';

const AdminDashboard = () => {
  const [view, setView] = useState('');
  const [flightView, setFlightView] = useState('');
  const [passengerView, setPassengerView] = useState('');
  const [arrivalsDeparturesView, setArrivalsDeparturesView] = useState('');

  const [arrivals, setArrivals] = useState([]);
  const [departures, setDepartures] = useState([]);
  const [passengers, setPassengers] = useState([]);

  const [newArrivalFlight, setNewArrivalFlight] = useState({ fno: '', fname: '', arr_time: '', origin: '', status: '' });
  const [newDepartureFlight, setNewDepartureFlight] = useState({ fno: '', fname: '', dept_time: '', loc: '', status: '' });
  const [newPassenger, setNewPassenger] = useState({ name: '', email: '', phone: '', flight: '', location: '', date: '', tstatus: '' });

  const [isEditingFlight, setIsEditingFlight] = useState(null);
  const [isEditingPassenger, setIsEditingPassenger] = useState(null);

  useEffect(() => {
    fetchArrivals();
    fetchDepartures();
    fetchPassengers();
  }, []);

  const fetchArrivals = async () => {
    const response = await axios.get('http://localhost:8080/Arrival/display'); 
    setArrivals(response.data);
  };

  const fetchDepartures = async () => {
    const response = await axios.get('http://localhost:8080/Departure/Display');
    setDepartures(response.data);
  };

  const fetchPassengers = async () => {
    const response = await axios.get('http://localhost:8080/Passenger/displaypass');
    setPassengers(response.data);
  };

  const handleAddArrivalFlight = async (e) => {
    e.preventDefault();
    try {
      if (isEditingFlight !== null) {
        await axios.put('http://localhost:8080/Arrival/Update', newArrivalFlight); 
      } else {
        await axios.post('http://localhost:8080/Arrival/Insert', newArrivalFlight);
      }
      fetchArrivals();
    } catch (error) {
      console.error(error);
    }
    setNewArrivalFlight({ fno: '', fname: '', arr_time: '', origin: '', status: '' });
    setIsEditingFlight(null);
  };

  const handleAddDepartureFlight = async (e) => {
    e.preventDefault();
    try {
      if (isEditingFlight !== null) {
        await axios.put(`http://localhost:8080/Departure/update`, newDepartureFlight);
      } else {
        await axios.post('http://localhost:8080/Departure/insert', newDepartureFlight);
      }
      fetchDepartures();
    } catch (error) {
      console.error(error);
    }
    setNewDepartureFlight({ fno: '', fname: '', dept_time: '', loc: '', status: '' });
    setIsEditingFlight(null);
  };

  const handleAddPassenger = async (e) => {
    e.preventDefault();
    try {
      if (isEditingPassenger !== null) {
        await axios.put(`http://localhost:8080/Passenger/Updatepass`, newPassenger);
      } else {
        await axios.post('http://localhost:8080/Passenger/Insertpass', newPassenger);
      }
      fetchPassengers();
    } catch (error) {
      console.error(error);
    }
    setNewPassenger({ name: '', email: '', phone: '', flight: '', location: '', date: '', tstatus: '' });
    setIsEditingPassenger(null);
  };

  const handleDeleteFlight = async (type, index) => {
    try {
      const flight = type === 'arrivals' ? arrivals[index] : departures[index];
      if (type === 'arrivals') {
        await axios.delete(`http://localhost:8080/Arrival/Delete/${flight.fno}`);
      } else {
        await axios.delete(`http://localhost:8080/Departure/delete/${flight.fno}`);
      }
      type === 'arrivals' ? fetchArrivals() : fetchDepartures();
    } catch (error) {
      console.error(error);
    }
  };

  const handleDeletePassenger = async (index) => {
    try {
      await axios.delete(`http://localhost:8080/Passenger/Deletepass/${passengers[index].phone}`);
      fetchPassengers();
    } catch (error) {
      console.error(error);
    }
  };

  const handleArrivalsDeparturesToggle = (type) => {
    setArrivalsDeparturesView(type);
    setFlightView('');
  };

  return (
    <div className="admin-dashboard">
      <div className="top-container">
        <button onClick={() => setView('flights')} className={view === 'flights' ? 'active' : 'inactive'}>
          Flights
        </button>
        <button onClick={() => setView('passengers')} className={view === 'passengers' ? 'active' : 'inactiv'}>
          Passengers
        </button>
      </div>

      <div className="section-content">
        {view === 'flights' && (
          <div>
            <div className="section-buttons">
              <button onClick={() => handleArrivalsDeparturesToggle('arrivals')}>Arrivals</button>
              <button onClick={() => handleArrivalsDeparturesToggle('departures')}>Departures</button>
            </div>

            {arrivalsDeparturesView && (
              <div className="section-buttons">
                <button onClick={() => setFlightView('add')}>Add {arrivalsDeparturesView === 'arrivals' ? 'Arrival' : 'Departure'} Flight</button>
                <button onClick={() => setFlightView('display')}>Display {arrivalsDeparturesView === 'arrivals' ? 'Arrival' : 'Departure'} Flights</button>
              </div>
            )}

            {/* Add Arrival flight form */}
            {flightView === 'add' && arrivalsDeparturesView === 'arrivals' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddArrivalFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Arrival Flight' : 'Add Arrival Flight'}</h2>
                  <div className="form-group">
                    <label>Arrival Time:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.arr_time}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, arr_time: e.target.value })}
                    />
                    <label>Flight Name:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.fname}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, fname: e.target.value })}
                    />
                    <label>Flight Number:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.fno}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, fno: e.target.value })}
                    />
                    <label>Origin:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.origin}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, origin: e.target.value })}
                    />
                    <label>Status:</label>
                    <input
                      type="text"
                      value={newArrivalFlight.status}
                      onChange={(e) => setNewArrivalFlight({ ...newArrivalFlight, status: e.target.value })}
                    />
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Add Departure flight form */}
            {flightView === 'add' && arrivalsDeparturesView === 'departures' && (
              <div className="flight-section">
                <form className="flight-form" onSubmit={handleAddDepartureFlight}>
                  <h2>{isEditingFlight !== null ? 'Edit Departure Flight' : 'Add Departure Flight'}</h2>
                  <div className="form-group">
                    <label>Departure Time:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.dept_time}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, dept_time: e.target.value })}
                    />
                    <label>Flight Name:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.fname}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, fname: e.target.value })}
                    />
                    <label>Flight Number:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.fno}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, fno: e.target.value })}
                    />
                    <label>Destination:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.loc}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, loc: e.target.value })}
                    />
                    <label>Status:</label>
                    <input
                      type="text"
                      value={newDepartureFlight.status}
                      onChange={(e) => setNewDepartureFlight({ ...newDepartureFlight, status: e.target.value })}
                    />
                    <button type="submit">{isEditingFlight !== null ? 'Update Flight' : '+ Add Flight'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Display flight list */}
            {flightView === 'display' && (
              <div className="flights-list">
                <h3>{arrivalsDeparturesView === 'arrivals' ? 'Arrival Flights' : 'Departure Flights'}:</h3>
                {(arrivalsDeparturesView === 'arrivals' ? arrivals : departures).map((flight, index) => (
                  <div key={index} className="data-row">
                    <span>
                      {arrivalsDeparturesView === 'arrivals' ? `Arrival Time: ${flight.arr_time}` : `Departure Time: ${flight.dept_time}`} 
                      - Flight Name: {flight.fname} 
                      - Flight Number: {flight.fno} 
                      - {arrivalsDeparturesView === 'arrivals' ? `Origin: ${flight.origin}` : `Destination: ${flight.loc}`} 
                      - Status: {flight.status}
                    </span>
                    <button className="edit-button" onClick={() => { 
                      setFlightView('add'); 
                      setIsEditingFlight(index); 
                      arrivalsDeparturesView === 'arrivals' ? setNewArrivalFlight(flight) : setNewDepartureFlight(flight); 
                    }}>Edit</button>
                    <button className="delete-button" onClick={() => handleDeleteFlight(arrivalsDeparturesView, index)}>Delete</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {view === 'passengers' && (
          <div>
            <div className="section-buttons">
              <button onClick={() => setPassengerView('add')}>Add Passenger</button>
              <button onClick={() => setPassengerView('display')}>Display Passengers</button>
            </div>

            {/* Add Passenger form */}
            {passengerView === 'add' && (
              <div className="passenger-section">
                <form className="passenger-form" onSubmit={handleAddPassenger}>
                  <h2>{isEditingPassenger !== null ? 'Edit Passenger' : 'Add Passenger'}</h2>
                  <div className="form-group">
                    <label>Name:</label>
                    <input
                      type="text"
                      value={newPassenger.name}
                      onChange={(e) => setNewPassenger({ ...newPassenger, name: e.target.value })}
                      required
                    />
                    <label>Email:</label>
                    <input
                      type="email"
                      value={newPassenger.email}
                      onChange={(e) => setNewPassenger({ ...newPassenger, email: e.target.value })}
                      required
                    />
                    <label>Phone:</label>
                    <input
                      type="text"
                      value={newPassenger.phone}
                      onChange={(e) => setNewPassenger({ ...newPassenger, phone: e.target.value })}
                      required
                    />
                    <label>Flight:</label>
                    <input
                      type="text"
                      value={newPassenger.flight}
                      onChange={(e) => setNewPassenger({ ...newPassenger, flight: e.target.value })}
                      required
                    />
                    <label>Location:</label>
                    <input
                      type="text"
                      value={newPassenger.location}
                      onChange={(e) => setNewPassenger({ ...newPassenger, location: e.target.value })}
                      required
                    />
                    <label>Travel Status:</label>
                    <input
                      type="text"
                      value={newPassenger.tstatus}
                      onChange={(e) => setNewPassenger({ ...newPassenger, tstatus: e.target.value })}
                      required
                    />
                    <label>Date:</label>
                    <input
                      type="date"
                      value={newPassenger.date}
                      onChange={(e) => setNewPassenger({ ...newPassenger, date: e.target.value })}
                      required
                    />
                    <button type="submit">{isEditingPassenger !== null ? 'Update Passenger' : '+ Add Passenger'}</button>
                  </div>
                </form>
              </div>
            )}

            {/* Display Passenger list */}
            {passengerView === 'display' && (
              <div className="passengers-list">
                <h3>Passenger List:</h3>
                {passengers.map((passenger, index) => (
                  <div key={index} className="data-row">
                    <span>Name: {passenger.name} - Email: {passenger.email} - Phone: {passenger.phone} - Flight: {passenger.flight} - Location: {passenger.location} - Date: {passenger.date} -Travel Status: {passenger.tstatus}</span>
                    <button onClick={() => { 
                      setPassengerView('add'); 
                      setIsEditingPassenger(index); 
                      setNewPassenger(passenger); 
                    }}>Edit</button>
                    <button onClick={() => handleDeletePassenger(index)}>Delete</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
