import React, { useState } from 'react';
import axios from 'axios';
import './home.css'; // Custom CSS for styling the home page
import { ButtonGroup, Button } from 'react-bootstrap';
import SideMenu from './SideMenu';

function Home() {
  const [arrivals, setArrivals] = useState([]);
  const [departures, setDepartures] = useState([]);
  const [displayData, setDisplayData] = useState([]); // State to hold the data to be displayed
  const [view, setView] = useState(''); // State to toggle between arrivals and departures

  const handleArrivals = async () => {
    try {
      const response = await axios.get('http://localhost:8080/Arrival/display'); // Fetch arrivals data
      setArrivals(response.data);
      setDisplayData(response.data); // Set data to be displayed
      setView('arrivals'); // Set view to arrivals
      console.log('Arrivals fetched successfully:', response.data);
    } catch (error) {
      console.error('Error fetching arrivals:', error);
    }
  };

  const handleDepartures = async () => {
    try {
      const response = await axios.get('http://localhost:8080/Departure/Display'); // Fetch departures data
      setDepartures(response.data);
      setDisplayData(response.data); // Set data to be displayed
      setView('departures'); // Set view to departures
      console.log('Departures fetched successfully:', response.data);
    } catch (error) {
      console.error('Error fetching departures:', error);
    }
  };

  return (
    <div className="home-container">
      <div className="overlay">
        <h1>Welcome to Airport Directory App</h1>
        <div className="button-group">
          <ButtonGroup>
            <Button variant="primary" onClick={handleArrivals}>ARRIVALS</Button>
            <Button variant="primary" onClick={handleDepartures}>DEPARTURES</Button>
          </ButtonGroup>
        </div>
        {/* Display data */}
        <div className="data-display">
          {view === 'arrivals' && (
            <div>
              <h2>Arrivals</h2>
              <ul>
                {displayData.map((flight, index) => (
                  <li key={index}>
                    Flight No: {flight.fno}, Arrival Time: {flight.arr_time}, Airline: {flight.flight}, Origin: {flight.origin}, Status: {flight.status}
                  </li>
                ))}
              </ul>
            </div>
          )}
          {view === 'departures' && (
            <div>
              <h2>Departures</h2>
              <ul>
                {displayData.map((flight, index) => (
                  <li key={index}>
                    Flight No: {flight.fno}, Departure Time: {flight.dept_time}, Airline: {flight.flight}, Destination: {flight.destination}, Status: {flight.status}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
      <SideMenu /> {/* Keep the SideMenu for the navigation links */}
    </div>
  );
}

export default Home;
