import React, { useState } from 'react';
import './adminaccess.css';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios


const AdminLogin = ({ closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/admin/login', {
        username,
        password
      });

      // If login is successful, navigate to the admin dashboard
      if (response.status === 200) {
        console.log('Login successful:', response.data);
        navigate('/admin-dashboard');
      }
    } catch (error) {
      console.error('Login failed:', error.response ? error.response.data : error.message);
      alert('Invalid username or password'); // You can display a more user-friendly message
    } /*finally {
      closeModal(); // Close modal regardless of success or failure
    }*/
  };

  const [isModalOpen,setIsModalOpen] = useState(false);

 

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="admin-modal">
      <div className="admin-modal-content">
        <h2>Admin Login</h2>
        <div id="sidemenu" className={ handleCloseModal? 'open' : 'closed'}>
        <i className="fas fa-times close-icon" onClick={closeModal}></i>
        </div> 
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <i className="bx bxs-user"></i>
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <i className="bx bxs-lock-alt"></i>
          </div>

          <button type="submit" className="btn">Login</button>
        </form>
      </div>
    </div>
  );
};

export default AdminLogin;
