import React, { useState } from 'react';
import './adminaccess.css';
import { useNavigate } from 'react-router-dom';

const AdminLogin = ({ closeModal }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isRegistering, setIsRegistering] = useState(false); // New state for toggling
  const navigate = useNavigate();

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    console.log('Admin Username:', username);
    console.log('Admin Password:', password);
    
    // After successful login, navigate to the admin dashboard
    navigate('/admin-dashboard');
    closeModal();
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    // Here you can add logic to handle registration
    console.log('New Username:', username);
    console.log('New Password:', password);
    
    // Close modal after successful registration
    closeModal();
  };

  return (
    <div className="admin-modal">
      <div className="admin-modal-content">
        <h2>{isRegistering ? 'Register' : 'Admin Login'}</h2>
        <span className="admin-close" onClick={closeModal}>&times;</span>
        <form onSubmit={isRegistering ? handleRegisterSubmit : handleLoginSubmit}>
          <div className="input-box">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <i className="bx bxs-user"></i>
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <i className="bx bxs-lock-alt"></i>
          </div>
          <div className="remember-forget">
            <label>
              <input type="checkbox" />
              Remember me
            </label>
            {!isRegistering && <a href="#">Forgot Password?</a>}
          </div>
          <button type="submit" className="btn">{isRegistering ? 'Register' : 'Login'}</button>
        </form>
        <div className="register-link">
          {isRegistering ? (
            <p>Already have an account? <a href="#" onClick={() => setIsRegistering(false)}>Login</a></p>
          ) : (
            <p>Don't have an account? <a href="#" onClick={() => setIsRegistering(true)}>Register</a></p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
